mapper.select:
select mstore.center_id, b1.branchname as centerName,mstore.line_id, lines.linename as lineName , mstore.store_id, b2.branchname as storeName, mstore.shortname as shortName, mstore.seq ,b2.openeddate as openedDate from centralkitchen.mt_centerdlvstore mstore inner join centralkitchen.branches b1 on mstore.center_id = b1.branchcd inner join centralkitchen.lines lines on mstore.line_id = lines.linecd inner join centralkitchen.branches b2 on mstore.store_id = b2.branchcd
mstore.center_id = #{param.centerId}
and mstore.line_id in
#{item}
and f_del = 0
order by mstore.seq asc
